// Extract and transform USAC filing data into Supabase-compatible format
const items = $input.all();
const transformedItems = [];

// Helper function to collect document links from RFP1-10 and AdditionalDoc1-10
function collectDocumentLinks(item) {
  const rfpLinks = [];
  const additionalDocs = [];

  // Collect RFP documents (RFP1 through RFP10)
  for (let i = 1; i <= 10; i++) {
    const rfpField = `rfp${i}`;
    if (item[rfpField] && item[rfpField].trim() !== '') {
      rfpLinks.push(item[rfpField].trim());
    }
  }

  // Collect Additional documents (AdditionalDoc1 through AdditionalDoc10)
  for (let i = 1; i <= 10; i++) {
    const docField = `additionaldoc${i}`;
    if (item[docField] && item[docField].trim() !== '') {
      additionalDocs.push(item[docField].trim());
    }
  }

  return {
    rfp_links: rfpLinks,
    additional_docs: additionalDocs
  };
}

// Transform each filing
for (const item of items) {
  const json = item.json;

  // Skip if missing critical fields
  if (!json.hcpnumber || !json.hcpname || !json.healthcaresite_state) {
    continue;
  }

  // Collect document links
  const documentLinks = collectDocumentLinks(json);

  // Create deduplication hash
  const hashString = `${json.hcpnumber}-${json.posting_date || ''}-${json.healthcaresite_addressline1 || ''}`;
  const form465Hash = hashString.replace(/\s+/g, '_').toLowerCase();

  // Transform to match Supabase schema
  const transformed = {
    // Core identification
    hcp_number: json.hcpnumber || '',
    clinic_name: json.hcpname || '',
    application_number: json.applicationnumber || '',
    funding_year: json.funding_year || json.fundingyear || '',
    application_type: json.application_type || json.applicationtype || '',

    // Address fields
    address: json.healthcaresite_addressline1 || '',
    city: json.healthcaresite_city || '',
    state: json.healthcaresite_state || '',
    zip: json.healthcaresite_zipcode || json.site_zip_code || '',

    // Filing information
    filing_date: json.posting_date || new Date().toISOString(),
    form_465_hash: form465Hash,
    form_465_pdf_url: json.linktofccformpdf || json.link_to_pdf || '',

    // Service details
    service_type: json.descriptionofservicesrequested || json.service_type || '',
    contract_length: json.requestedcontractperiod ? parseInt(json.requestedcontractperiod) : null,
    allowable_contract_start_date: json.allowablecontractstartdate || null,
    bandwidth: json.bandwidth || '',

    // Primary contact information
    contact_name: json.contact_firstname && json.contact_lastname 
      ? `${json.contact_firstname} ${json.contact_lastname}`.trim()
      : json.contact_name || '',
    contact_email: json.contact_email || '',
    contact_phone: json.contact_phonenumber || json.contact_phone || '',

    // Mail contact information
    mail_contact_first_name: json.mailcontact_firstname || '',
    mail_contact_last_name: json.mailcontact_lastname || '',
    mail_contact_org_name: json.mailcontact_orgname || '',
    mail_contact_phone: json.mailcontact_phone || '',
    mail_contact_email: json.mailcontact_email || '',

    // Document links as JSONB
    additional_documents: documentLinks,

    // Processing status defaults
    processed: false
  };

  transformedItems.push({ json: transformed });
}

console.log(`Transformed ${transformedItems.length} filings from ${items.length} input items`);

return transformedItems;

// Merge historical funding data with clinic record
const clinicData = $('Loop Over Each Clinic').item.json;
const historicalData = $input.all();

// Collect historical funding data into JSONB array
const historicalFunding = [];
for (const item of historicalData) {
  const json = item.json;
  if (json.funding_year && json.original_requested_amount) {
    historicalFunding.push({
      year: json.funding_year,
      amount: parseFloat(json.original_requested_amount) || 0
    });
  }
}

// Merge with clinic data
const enrichedRecord = {
  ...clinicData,
  historical_funding: historicalFunding
};

console.log(`Enriched ${clinicData.hcp_number} with ${historicalFunding.length} historical funding records`);

return [{ json: enrichedRecord }];

// Log workflow completion and summary statistics
const allItems = $input.all();
const successCount = allItems.length;

const timestamp = new Date().toISOString();
const summary = {
  workflow: 'Phase 1: USAC Data Pull and Enrichment',
  timestamp: timestamp,
  filings_processed: successCount,
  status: 'completed',
  message: `Successfully processed ${successCount} Form 465 filings and inserted into Supabase`
};

console.log('=== WORKFLOW COMPLETION ===');
console.log(JSON.stringify(summary, null, 2));
console.log('==========================');

// Return summary for potential webhook/notification
return [{ json: summary }];
